INSERT INTO `produit` (`ID_PRODUIT`, `NOM_PRODUIT`, `DEF_PRODUIT`, `REM_PRODUIT`, `DESCRIPTION`, `CATEGORIE`, `FAMILLE`, `PRIX`, `TAXE`, `IMAGE`, `IMG`, `ALT`, `DOCUMENT`, `IMG_DOCUMENT`, `VIDEO`, `QUANTITY`, `VISIBLE`) VALUES
(1, 'NYB', 'Boîtier PC industriel', '', '<li>Processeurs Intel® (depuis l&#39;Atom® jusqu&#39;au Xeon®)&#13;&#10;</li>\r\n<li>Windows 10 IoT Enterprise, Windows Embedded Standard 7 ou sans système d&#39;exploitation&#13;&#10;*\r\n</li>Système d&#39;exploitation ouvert afin d&#39;utiliser vos propres logiciels &#13;&#10;\r\n$\\surd$ Port Ethernet pour accéder à vos systèmes informatiques&#13;&#10;*Facteur de forme optimisé : options à 1, 2 ou 3 couches&#13;&#10;*Processeurs Intel® (depuis l&#39;Atom® jusqu&#39;au Xeon®)&#13;&#10;Windows 10 IoT Enterprise, Windows Embedded Standard 7 ou sans système d&#39;exploitation&#13;&#10;*Système d&#39;exploitation ouvert permettant le fonctionnement de logiciels et de matériel personnalisés&#13;&#10;*Port Ethernet pour accéder à vos systèmes informatiques&#13;&#10;*Moniteur industriel 15,4 et 12,1 pouces&#13;&#10;Boîtier PC industriel et armoire PC industrielle (boîtier PC industriel + moniteur intégré)&#13;&#10;*Modèles disponibles avec interface du câble pour moniteur NY, pour une liaison jusqu&#39;à 100 m entre l&#39;IPC et l&#39;écran&#13;&#10;*Matériel RAID en option', 1, 1, 0.000, 19.00, 'assets\\img\\automatisation\\NYB.png', NULL, '', NULL, NULL, NULL, 100, 'V'),
(2, 'NYP', 'Armoire PC industriel', '', 'Processeurs Intel® (depuis le Atom® jusqu\'au processeur Intel® Xeon®) \r\nWindows 10 IoT Enterprise, Windows Embedded Standard 7 ou sans système d\'exploitation\r\nSystème d\'exploitation ouvert afin d\'utiliser vos propres logiciels \r\nPort Ethernet pour accéder à vos systèmes informatiques.\r\nFacteur de forme optimisé : options à 1, 2 ou 3 couches\r\nProcesseurs Intel® (depuis le Atom® jusqu\'au processeur Intel® Xeon®) \r\nWindows 10 IoT Enterprise, Windows Embedded Standard 7 ou sans système d\'exploitation\r\nSystème d\'exploitation ouvert permettant le fonctionnement de logiciels et de matériel personnalisés\r\nPort Ethernet pour accéder à vos systèmes informatiques\r\nMoniteur industriel 15,4 et 12,1 pouces\r\nBoîtier PC industriel et armoire PC industrielle (boîtier PC industriel + moniteur intégré)\r\nModèles disponibles avec interface du câble pour moniteur NY, pour une liaison jusqu\'à 100 m entre l\'IPC et l\'écran', 1, 1, 0.000, 19.00, 'assets\\img\\automatisation\\NYP.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(3, 'NY5', 'Contrôleur machine IPC', 'Contrôleur hybride qui combine le contrôle des machines Sysmac et la technologie informatique', 'Processeur Intel Core i5 à 4 cœurs\r\nWindows 10 IoT Enterprise 2019 LTSC\r\nSystème d\'exploitation ouvert permettant le fonctionnement de logiciels et de matériel personnalisés\r\nFonctions : séquence logique, contrôle d\'axes et NC\r\nPort IP/Ethernet intégré pour vos systèmes informatiques et la communication de machine à machine\r\nContrôleur de machine Sysmac intégré\r\nTemps de cycle système : 500 µs\r\nJusqu\'à 64 axes synchronisés\r\nPort EtherCAT intégré pour synchroniser jusqu\'à 192 esclaves\r\nFonctionnalité NC disponible sur armoire PC industrielle* : idéale pour la découpe XY, le fraisage, le tournage et toute autre application avec suivi de trajectoire telles que les machines de dépose de colle, de pliage, de meulage et de formage', 1, 1, 0.000, 19.00, 'assets\\img\\automatisation\\NY5.png', NULL, '', NULL, NULL, NULL, 100, 'V'),
(4, 'NYM', 'Moniteur industriel', 'Affichage et interface tactile pour la plateforme de PC industriels.', 'Modèles de 12,1, 15,4 et 18,5 pouces &#13;&#10;Multi-touch, à l’aide de la dernière technologie de capacité projetée&#13;&#10;Détection de faux contact (rejet de paume, eau et nettoyage)&#13;&#10;Fonctionnement avec des gants (En cas d’utilisation de gants, s’assurer que les gants utilisés sont fonctionnels avec cet écran tactile)&#13;&#10;Montage facile intégré&#13;&#10;Modèles disponibles avec interface NY Monitor Link pour une connectivité jusqu’à 100 mètres entre le boîtier PC et le moniteur', 1, 1, 0.000, 19.00, 'assets\\img\\automatisation\\NYM.png', NULL, '', NULL, NULL, NULL, 100, 'V'),
(8, 'A165E', 'Interrupteur d\'arrêt d\'urgence', 'La gamme A165E propose des interrupteurs d\'arrêt d\'urgence avec différents modèles de têtes. Pour un', 'Mécanisme d\'ouverture directe avec séparation minimale des contacts de 3 mm\r\nUn mécanisme de verrou de sécurité permet d\'éviter toute mauvaise manipulation\r\nFaible profondeur de montage\r\nConstruction modulaire ; installation facile avec interrupteur embrochable', 4, 14, 0.000, 19.00, 'assets\\img\\Sécurité\\A165E.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(10, 'CX-One', 'Un logiciel unique pour votre système d\'automatisa', 'La suite logicielle CX-One permet aux utilisateurs de créer, de configurer et de programmer différen', 'Un logiciel unique pour programmer les API + IHM, les réseaux, le contrôle d\'axes et la variation de fréquence, la régulation et la commutation et les capteurs\r\nInterface similaire pour la configuration de tous les périphériques\r\nUne installation et un numéro de licence uniques pour toute la suite logicielle\r\nL\'enregistrement sur Internet donne accès gratuitement aux mises à jour, à des bibliothèques, ainsi qu\'à l\'aide et aux réponses aux questions fréquentes', 8, 45, 0.000, 0.00, 'assets\\img\\Sécurité\\cx-one.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(14, 'A22E/A22NE-P/A22NE-PD', 'Interrupteur d’arrêt d’urgence', 'La gamme A22E/A22NE-P/A22NE-PD offre des interrupteurs d\'arrêt d\'urgence avec différents modèles de ', 'Mécanisme d\'ouverture directe pour ouvrir le circuit en cas de soudure des contacts\r\nMécanisme de verrouillage pour éviter les erreurs de fonctionnement\r\nLevier de verrouillage pour monter et retirer l\'unité facilement\r\nUtilisation d\'un anneau de 25 de diamètre pour l\'installation dans des découpes d\'armoire d\'un diamètre de 25.*\r\nSur un modèle doté d\'un levier de verrouillage lié au contact, l\'installation incorrecte de l\'unité de commutation peut être détectée. (A22NE-PD)\r\n*Le diamètre de 25 ne peut pas être utilisé avec les interrupteurs garantis sous la classe de protection IP69K.', 4, 14, 0.000, 19.00, 'assets\\img\\Sécurité\\A22EA22NE-PA22NE-PD.jpg', NULL, '', NULL, NULL, NULL, 100, 'I'),
(15, 'NX7', 'Contrôleur Sysmac – Série NX7', 'La série NX7 est une UC haute performance qui inclut deux contrôles d\'axes synchronisés contrôlant jusqu\'à 256 axes.', 'Temps de cycle court : 125 µs&#13;&#10;Nombre d&#39;axes : 256, 128&#13;&#10;Deux contrôles d&#39;axes synchronisés&#13;&#10;Fonctions : séquence logique, connexion à la base de données, intelligence artificielle et contrôle d&#39;axes&#13;&#10;Connexion directe à la base de données : client SQL pour serveur Microsoft SQL, Oracle, IBM DB2, MySQL, Firebird, PostgreSQL&#13;&#10;Connexion directe à la base de données en V2.0 étendue avec appels à des procédures stockées et fonctionnalité de communication sécurisée&#13;&#10;Fonctionnalité IA : le moteur IA compris dans le contrôleur permet une utilisation du modèle d&#39;apprentissage machine en temps réel&#13;&#10;Le système de bibliothèque de maintenance préventive IA permet un équipement en continu&#13;&#10;Multitâche&#13;&#10;Un port EtherCAT et deux ports EtherNet/IP (1 Gbit/s) intégrés&#13;&#10;Compatible IoT : serveur OPC UA intégré en standard, structures variables prises en charge', 1, 4, 0.000, 19.00, 'assets/img/produit78e9639f9815be4ffe16a458c4170849', NULL, '', NULL, NULL, NULL, 100, 'V'),
(16, 'NX1', 'Information et productivité réunies dans un contrôleur miniature', 'Le contrôleur de machine modulaire série NX1 présente des fonctionnalités de séquence logique, de contrôle d\'axes et d\'information. Le NX1 réunit le meilleur de la production et de l\'informatique pour réduire l\'ingénierie et la maintenance tout en éliminant le besoin d\'intergiciel.', 'Compatible IoT : serveur OPC UA intégré en standard, structures variables prises en charge&#13;&#10;Connexion directe à la base de données : client SQL pour serveur Microsoft SQL, Oracle, IBM DB2, MySQL, Firebird, PostgreSQL&#13;&#10;Grande capacité de mémoire pour le traitement des données. 33,5 Mo pour les variables&#13;&#10;Contrôleur sans batterie&#13;&#10;Temps de cycle : à partir de 1 ms&#13;&#10;Jusqu&#39;à 12 axes (8 axes synchronisés et 4 PTP)&#13;&#10;Cycle unique et synchronisé pour les unités E/S locales ou déportées, les appareils EtherCAT, et le contrôle d&#39;axes&#13;&#10;Connexion directe à la base de données en V2.0 étendue avec appels à des procédures stockées et fonctionnalité de communication sécurisée&#13;&#10;Connexion directe à la base de données : client SQL pour serveur Microsoft SQL, Oracle, IBM DB2, MySQL, Firebird, PostgreSQL&#13;&#10;Contrôleur multitâche&#13;&#10;Connectivité réseau élargie, 2 ports Ethernet + 1 EtherCAT&#13;&#10;Jusqu&#39;à 32 unités NX locales, dont sécurité intégrée et unité maître IO-Link&#13;&#10;Prise en charge d&#39;un total de 64 nœuds EtherCAT (400 unités NX)&#13;&#10;La topologie EtherCAT en anneau est prise en charge afin de maintenir les communications et les contrôles en cas de rupture de câble ou de panne de périphérique', 1, 4, 0.000, 19.00, 'assets/img/produit725d42f553271dced45ae738c5c52b41', NULL, '', NULL, NULL, NULL, 100, 'V'),
(18, 'NX1P', 'Compact par la taille, riche en fonctionnalités', 'Le NX1P vient compléter la gamme de contrôleurs de machines NX/NJ en offrant les mêmes fonctionnalités en un design compact. Le NX1P offre un contrôle synchronisé de tous les périphériques de la machine, tels que le mouvement, les E/S, la sécurité et la vision, dans un seul environnement de développement.', 'Temps de cycle machine très rapide : 2 ms&#13;&#10;Fonctionnalités : séquence logique et contrôle d&#39;axes&#13;&#10;Jusqu&#39;à 8 axes (4 axes synchronisés)&#13;&#10;Points d&#39;E/S intégrés : 40 ou 24 points d&#39;E/S&#13;&#10;Jusqu&#39;à 8 cartes E/S NX locales&#13;&#10;Ports EtherCAT et EtherNet/IP intégrés&#13;&#10;Jusqu&#39;à 16 esclaves EtherCAT&#13;&#10;Jusqu&#39;à 2 cartes en option peuvent être connectées pour ajouter des communications série ou des fonctionnalités d&#39;E/S analogiques&#13;&#10;La topologie EtherCAT en anneau est prise en charge afin de maintenir les communications et les contrôles en cas de rupture de câble ou de panne de périphérique', 1, 4, 0.000, 19.00, 'assets/img/produitf1c9844f9cc3b82c93ce3beb469f6075', NULL, '', NULL, NULL, NULL, 100, 'V'),
(19, 'NJ5', 'Contrôleur Sysmac – Série NJ5', 'La série NJ5 est un contrôleur de machine pour la séquence logique et le contrôle d\'axes qui inclut des UC pour les options avancées, telles que la robotique et la connexion à une base de données.', 'Temps de cycle court : 500 µs&#13;&#10;Nombre d&#39;axes : 64, 32, 16&#13;&#10;Contrôle d&#39;axes synchronisé&#13;&#10;Fonctionnalités : séquence logique, contrôle d&#39;axes, CNC, robotique, connexion à une base de données et SECS/GEM et protocole OPC-UA&#13;&#10;Connexion directe à la base de données en V2.0 étendue avec appels à des procédures stockées et fonctionnalité de communication sécurisée&#13;&#10;Connexion directe à la base de données : client SQL pour serveur Microsoft SQL, Oracle, IBM DB2, MySQL, Firebird, PostgreSQL&#13;&#10;Commande de robots Delta, SCARA et cartésiens&#13;&#10;Fonctionnalité CNC : idéale pour la découpe XY, le fraisage, le tournage et toute autre application avec suivi de trajectoire telle que les machines de dépose de colle, de pliage, de meulage et de formage&#13;&#10;Multitâche&#13;&#10;Ports EtherCAT et EtherNet/IP intégrés&#13;&#10;La topologie EtherCAT en anneau est prise en charge afin de maintenir les communications et les contrôles en cas de rupture de câble ou de panne de périphérique', 1, 4, 0.000, 19.00, 'assets/img/produitd6029876c872cd5463bca91d0c9af7b7', NULL, '', NULL, NULL, NULL, 100, 'V'),
(20, 'NJ3', 'Contrôleur Sysmac – Série NJ3', 'La série NJ3 permet au contrôleur Sysmac de contrôler 4 ou 8 axes.', 'Temps de cycle court : 500 µs&#13;&#10;Nombre d&#39;axes : 8, 4&#13;&#10;Contrôle d&#39;axes synchronisé&#13;&#10;Fonctionnalités : séquence logique et contrôle d&#39;axes&#13;&#10;Multitâche&#13;&#10;Ports EtherCAT et EtherNet/IP intégrés&#13;&#10;La topologie EtherCAT en anneau est prise en charge afin de maintenir les communications et les contrôles en cas de rupture de câble ou de panne de périphérique', 1, 4, 0.000, 19.00, 'assets/img/produit37b9129a44144cbedf665667701fdaea', NULL, '', NULL, NULL, NULL, 100, 'V'),
(21, 'NJ1', 'Contrôleur Sysmac – Série NJ1', 'La série NJ1 est un nouveau contrôleur Sysmac destiné au contrôle de séquence logique et d\'axes des machines compactes.', 'Temps de cycle machine très rapide : 1 ms&#13;&#10;Nombre d&#39;axes : 2&#13;&#10;Contrôle d&#39;axes synchronisé&#13;&#10;Fonctions : séquence logique, contrôle d&#39;axes et connexion à une base de données&#13;&#10;Connexion directe à la base de données en V2.0 étendue avec appels à des procédures stockées et fonctionnalité de communication sécurisée&#13;&#10;Connexion directe à la base de données : client SQL pour serveur Microsoft SQL, Oracle, IBM DB2, MySQL, Firebird, PostgreSQL&#13;&#10;Multitâche&#13;&#10;Ports EtherCAT et EtherNet/IP intégrés&#13;&#10;Connexion à une base de données : Microsoft SQL server, Oracle, IBM DB2, MySQL, Firebird&#13;&#10;La topologie EtherCAT en anneau est prise en charge afin de maintenir les communications et les contrôles en cas de rupture de câble ou de panne de périphérique', 1, 4, 0.000, 19.00, 'assets/img/produitc036c0d7faca60480ed6af57d6dc5c6c', NULL, '', NULL, NULL, NULL, 100, 'V'),
(22, 'NY5', 'Contrôleur de machine IPC', 'Contrôleur hybride qui combine le contrôle des machines Sysmac et la technologie informatique', 'Processeur Intel Core i5 à 4 cœurs&#13;&#10;Windows 10 IoT Enterprise 2019 LTSC&#13;&#10;Système d&#39;exploitation ouvert permettant le fonctionnement de logiciels et de matériel personnalisés&#13;&#10;Fonctions : séquence logique, contrôle d&#39;axes et NC&#13;&#10;Port IP/Ethernet intégré pour vos systèmes informatiques et la communication de machine à machine&#13;&#10;Contrôleur de machine Sysmac intégré&#13;&#10;Temps de cycle système : 500 µs&#13;&#10;Jusqu&#39;à 64 axes synchronisés&#13;&#10;Port EtherCAT intégré pour synchroniser jusqu&#39;à 192 esclaves&#13;&#10;Fonctionnalité NC disponible sur armoire PC industrielle* : idéale pour la découpe XY, le fraisage, le tournage et toute autre application avec suivi de trajectoire telles que les machines de dépose de colle, de pliage, de meulage et de formage&#13;&#10;*Version NC : processeur Core i7 avec Windows Embedded Standard 7 64 bit', 1, 4, 0.000, 19.00, 'assets/img/produitb43d66a9b970f9468d3e86c5ecd19c21', NULL, '', NULL, NULL, NULL, 100, 'V'),
(23, 'CJ2', 'CJ2 PLC: Contrôleur modulaire&#13;&#10;hautes perf', 'En raison de sa grande flexibilité de configuration, CJ2 constitue la solution idéale pour une large', 'Accessible en permanence via le support de connexion USB de série&#13;&#10;Port Ethernet standard avec fonction Ethernet/IP Data Link&#13;&#10;Capacité du programme élevée allant de 5 000 à 400 000 pas&#13;&#10;Meilleure précision pour le fonctionnement de la machine et la qualité du traitement&#13;&#10;Le rafraîchissement immédiat des E/S basiques garantit un traitement en temps réel&#13;&#10;Capacité de mémoire de données élevée pouvant atteindre 832 000 mots&#13;&#10;Carte série en option&#13;&#10;Une mémoire bloc fonction dédiée garantit l&#39;efficacité de l&#39;exécution des modules logiciels de bloc fonction.', 1, 2, 0.000, 19.00, 'assets/img/produit6ac59ccaf24a04690cfa0282e05e91cf', NULL, '', NULL, NULL, NULL, 100, 'V'),
(24, 'CP2E', 'Contrôleur compact série CP2', 'Le micro-API est conçu pour les équipements compacts qui peuvent prendre en charge la collecte de do', 'Réduction des coûts de câblage et amélioration des performances de communication grâce au commutateur Ethernet 2 ports intégré et aux blocs de fonction dédiés. Vous pouvez économiser du matériel supplémentaire et simplifier la connectivité Ethernet avec un câblage Ethernet en guirlande pour la communication entre machines.&#13;&#10;La collecte, le contrôle et la surveillance des données des périphériques série sont facilités : jusqu&#39;à 3 ports série sont disponibles.&#13;&#10;Améliorez l&#39;efficacité de la conception et la productivité en réduisant le temps de développement grâce à une conception modulaire de la machine. Le CP2E comprend des fonctions de positionnement à 4 axes avec interpolation linéaire et bibliothèques Omron pour le positionnement, la communication entre machines et la maintenance prédictive.&#13;&#10;Installez-oubliez. Omron CP2E peut fournir une solution fiable pour toutes les conditions environnementales : plage de températures de fonctionnement étendue, fonctionnement sans batterie, indicateurs LED de borne d&#39;entrée/sortie pour un dépannage rapide, récupération automatique par interférences électriques.&#13;&#10;Complétez votre configuration avec l&#39;IHM NB pour une alliance parfaite.', 1, 2, 0.000, 19.00, 'assets/img/produit518c5e2865cfcba16bd60ed2b32d116b', NULL, '', NULL, NULL, NULL, 100, 'V'),
(25, 'CP1L', 'Contrôleur de machine compacte,&#13;&#10;aux perfo', 'Lorsqu&#39;on en vient à parler de contrôleurs de machines compactes, notre série CP1L offre la compacité d’un micro-API associée aux capacités d’un API modulaire. Elle offre toutes les fonctionnalités dont vous avez besoin pour contrôler vos machines, y compris une capacité de positionnement hors pair. En outre, certains modèles CP1L présentent un port Ethernet intégré avec fonctionnalité de services socket, ceci afin de vous offrir une connectivité flexible à des fins de surveillance, de commande, de consignation des données et d&#39;accès à distance. Le CP1L est complété par une gamme de cartes en option pour la communication série ou les E/S analogiques, ainsi que par d&#39;autres unités d&#39;extensions si vous avez besoin d&#39;E/S supplémentaires. La série CP1E partageant la même architecture que les séries CP1H, CJ1 et CS1, les programmes sont compatibles pour l’affectation de mémoire et les instructions.', '                       ', 1, 2, 0.000, 19.00, 'assets/img/produit8833d01974d25af48884587dd6ff41d9', NULL, '', NULL, NULL, NULL, 100, 'V'),
(26, 'CP1E', 'Le contrôleur de machine économique', 'Notre série CP1E offre toutes les fonctionnalités dont vous avez besoin pour contrôler des applications relativement simples, notamment une capacité de positionnement exceptionnelle.', 'Gamme comprenant des UC avec trois ports intégrés : USB, RS-232C, RS-485.&#13;&#10;La profondeur des UC avec connecteurs RS-232C est réduite de 20 mm. (N30/40/60S(1))&#13;&#10;Connexion facile aux ordinateurs à l&#39;aide de câbles USB disponibles dans le commerce.&#13;&#10;Avec les UC E30/40/60(S), N30/40/60(S@) ou NA20, ajoutez des E/S, des E/S analogiques ou des entrées de température par connexion', 1, 2, 0.000, 19.00, 'assets/img/produitc90c7a77f2214c82b7eb020e7aabb6bd', NULL, '', NULL, NULL, NULL, 100, 'V'),
(27, 'CP1H', 'Le contrôleur de machine compacte le plus rapide d', 'Conçu pour des machines compactes, il combine la taille d&#39;un micro API à la puissance d&#39;un API modulaire. Quatre compteurs grande vitesse intégrés et quatre sorties d&#39;impulsions conviennent parfaitement au contrôle de position multiaxes. Le CP1H-XA possède 4 entrées analogiques et 2 sorties analogiques intégrées. Il est ainsi adapté à la régulation de boucle simple, grâce à la fonction de contrôle PID avancé avec autoréglage de l&#39;API. Le CP1H peut être étendu avec les E/S CP1W et prend en charge jusqu&#39;à 2 cartes E/S spéciales CJ1. Cela signifie qu&#39;il est compatible avec les bus de terrain courants et prend en charge toutes les cartes de communication de la série CJ1.', 'Jusqu&#39;à 1 MHz pour les entrées/sorties&#13;&#10;Vitesse d&#39;exécution et jeu d&#39;instructions compatibles avec le CJ1M&#13;&#10;4 entrées analogiques et 2 sorties analogiques pour le modèle XA&#13;&#10;Port USB pour faciliter les communications, la programmation et la configuration&#13;&#10;Prend en charge PROFIBUS, DeviceNet, CAN et Ethernet', 1, 2, 0.000, 19.00, 'assets\\img\\automatisation\\cp1h_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(28, 'CS1D', 'Le système API à double redondance&#13;&#10;pour u', 'Le CS1D ajoute différentes options de double redondance à l&#39;architecture éprouvée du CS1 pour garantir un fonctionnement 24 heures sur 24 sans temps d&#39;arrêt. Des UC duplex, avec ou sans fonctions de contrôle de boucle, sont soumises en continu à des vérifications croisées, sans obligation pour l&#39;utilisateur d&#39;écrire des programmes spéciaux. Une façon encore plus simple d&#39;augmenter la disponibilité du système consiste à utiliser des cartes à double alimentation. L&#39;échange à chaud des UC, alimentations et cartes d&#39;E/S autorise une maintenance du système avec peu ou pas d&#39;interruption du processus.', 'Choisissez le niveau de redondance requis ; UC, alimentation, communication&#13;&#10;Aucun outil de programmation ou utilitaire logiciel spécial n&#39;est requis&#13;&#10;Echange à chaud des cartes d&#39;E/S&#13;&#10;Montage possible de toutes les cartes d&#39;E/S standard CS1&#13;&#10;Idéal pour le contrôle des générateurs de puissance, des équipements d&#39;alimentation en eau, la production 24 h/24 et 7 J/7', 1, 2, 0.000, 19.00, 'assets/img/produite6aec9ff57433c3a1d01877a5f96d5b7', NULL, '', NULL, NULL, NULL, 100, 'I'),
(29, 'E2B', 'La solution idéale pour les\r\nenvironnements industriels standard', 'Grâce à sa conception simple et au processus de production Omron « hot melt » innovant, le capteur E2B offre un très haut niveau de fiabilité et un rapport qualité/prix imbattable.', '<li>Indicateur toujours visible </li>\r\n<li> Numéro de série imprimé au laser </li>\r\n<li> Résistance aux vibrations et aux chocs : IEC 60947-5-2 (10 - 55 Hz)</li>\r\n<li>Température de fonctionnement : -25à 70°C </li>\r\n<li> Étanchéité : IP67</li>', 5, 25, 0.000, 0.00, 'assets\\img\\Détection\\Détecteurs inductifs\\e2b\\e2b_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(30, 'TL-W', 'Détecteur inductif plat en\r\nboîtier plastique compact', 'La famille TL-W offre une grande variété de détecteurs inductifs en bloc pour des montages simples sur des surfaces plates. Avec des distances de détection allant de 1,5 mm à 20 mm, le TL-W est idéal pour les applications standard.', '<li> Surfaces de détection frontales ou latérales  </li>\r\n<li> IP67  </li>\r\n<li> Modèles c.c. à 2 et 3 fils  </li>\r\n<li> Distances de détection de 1,5 à 20 mm  </li>', 5, 25, 0.000, 0.00, 'assets\\img\\Détection\\Détecteurs inductifs\\tl-w\\tl-w5f1_img_prod.jpg\r\n\r\n', NULL, '', NULL, NULL, NULL, 100, 'V'),
(31, 'µPROX E2E', 'Détecteur de proximité\r\npetit diamètre pour une\r\ndétection haute précision', 'Les toutes dernières technologies Omron en matière d\'induction sont désormais appliquées à une nouvelle gamme de capteurs inductifs à petit diamètre. Le nouveau µPROX E2E offre une détection haute précision et peut être installé dans les espaces les plus réduits. Le portefeuille de produits a été élargi pour inclure les modèles non blindés et ceux dotés de câbles connecteurs en tire-bouchon.', '<li>Format miniature : 3, 4, 6,5 mm et diamètres M4 et M5  </li>\r\n<li> Haute fréquence de 5 kHz : adaptée pour les comptages à grande vitesse </li>\r\n<li> Tous ces formats sont également disponibles pour les modèles non blindés </li>\r\n<li> Étanchéité IP67 </li>\r\n<li> Indicateurs toujours visibles pour des confirmations d\'opération </li>', 5, 25, 0.000, 0.00, 'assets\\img\\Détection\\Détecteurs inductifs\\µPROX E2E\\e2e_dsc5051_20130311-bewerkt_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(32, 'E2A-4', 'Détecteur de proximité inductif\r\navec broches plaquées or', 'Le détecteur de proximité inductif E2A-4 a été conçu et testé pour des applications dans des environnements hostiles et des conditions de vibrations importantes. Les broches de contact plaquées or garantissent une meilleure protection contre la corrosion dans des environnements très humides et soumis à de fortes vibrations.', '<li>Broches de contact plaquées or </li>\r\n<li>Modèles de connecteurs de type M8 et M12 </li>\r\n<li>PNP/NPN NO </li>', 5, 25, 0.000, 0.00, 'assets\\img\\Détection\\Détecteurs inductifs\\e2a-4\\e2a_press_image_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(33, 'E2A-S', 'Détecteur inductif à portée\r\nétendue dans un boîtier\r\ncylindrique en acier inoxydable', 'Les performances et la fiabilité de fonctionnement de la gamme E2A sont également disponibles dans un boîtier en acier inoxydable.', '<li> boîtier en acier inoxydable (SUS 303) </li> ', 5, 25, 0.000, 0.00, 'assets\\img\\Détection\\Détecteurs inductifs\\e2a-s\\e2a-m18_img_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(34, 'E2EQ NEXT', 'Détecteur de proximité E2EQ NEXT', 'La détection longue distance empêche les pannes d\'installation', '<li>Avec revêtement résistant aux projections pour une application dans les zones de soudure </li>\r\n<li> Distance de détection presque doublée par rapport aux précédents modèles</li>\r\n<li>Grâce à sa LED à haute luminosité, l\'indicateur est visible n\'importe où à 360°. </li>\r\n<li> 10 secondes suffisent pour remplacer un détecteur de proximité grâce au « e-jig » (support de montage).</li>\r\n<li>Câbles avec résistance à l\'huile améliorée. </li>', 5, 25, 0.000, 0.00, 'assets\\img\\Détection\\Détecteurs inductifs\\E2EQ NEXT\\e2eq_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(35, 'E2EW', 'Détecteurs de proximité\r\nà face métallique', 'Détection stable de l aluminium et du fer', '<li> Boîtier entièrement métallique robuste </li><li> Distance de détection plus longue, jusqu à quadruplée </li><li> Modèles haut de gamme avec la même distance de détection pour le fer et l\'aluminium </li><li> Immunité au champ magnétique sur les modèles haut de gamme </li><li>  </li><li> IO-Link pour les modèles haut de gamme </li><li> Modèles avec revêtement résistant à la soudure disponibles </li><li> Visualisation lisible de l état, grâce à un indicateur LED haute luminosité visible à 360 ° </li><li> Distance de détection de l impression laser sur la face active pour éviter les erreurs de remplacement </li><li>Modèles à connecteur, câble et cordon spiralé disponibles  </li><li> Certification UL et CSA </li> ', 5, 25, 0.000, 0.00, 'assets\\img\\Détection\\Détecteurs inductifs\\e2ew\\e2ew_400x400_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(37, 'E2E NEXT', 'Détecteur de proximité E2E NEXT', 'La détection longue distance empêche les pannes d\'installation inopinées', '<li>Distance de détection la plus élevée au monde </li>\r\n<li> Grâce à sa LED à haute luminosité, l\'indicateur est visible n\'importe où à 360°.</li>\r\n<li>10 secondes suffisent pour remplacer un détecteur de proximité grâce au « e-jig » (support de montage). </li>\r\n<li>Câbles présentant une résistance à l\'huile améliorée pour 2 ans de résistance à l\'huile</li>\r\n<li> Conformité IP69K pour l\'étanchéité à l\'eau et la résistance au lavage</li>\r\n<li>Grande variation pour faciliter la sélection du détecteur </li>\r\n<li>Certification UL et CSA </li>', 5, 25, 0.000, 0.00, 'assets\\img\\Détection\\Détecteurs inductifs\\E2E NEXT\\e2e_next_3-wire_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(38, 'E2S', 'Détecteur inductif carré\r\nminiature en boîtier plastique', 'La gamme E2S propose des boîtiers miniatures en plastique pour les montage simples sur des surfaces planes. Le boîtier robuste en plastique avec surface de détection à l\'avant ou latérale offre une performance optimale pour la détection de mouvement de pièces sur les machines.', '<li>Boîtier miniature avec longues portées  </li>\r\n<li> Surfaces de détection frontales et latérales </li>\r\n<li> Montage simple avec une seule vis </li>\r\n<li> IP67 </li>', 5, 25, 0.000, 0.00, 'assets\\img\\Détection\\Détecteurs inductifs\\E2s\\e2s_img_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(39, 'E2Q5', 'Détecteur de proximité longue\r\nportée en boîtier plastique', 'La longue portée et la facilité d\'installation sur les surfaces plates font du E2Q5 le choix idéal pour la détection de grands objets métalliques (par ex. sur les lignes d\'assemblage de l\'industrie automobile).', '<li>Connexion Plug-In M12 </li>\r\n<li> SProtections intégrées contre les courts-circuits et les inversions de polarité </li>\r\n<li> Position active: as de 15° axe Y, 90° axe X </li>', 5, 25, 0.000, 0.00, 'assets\\img\\Détection\\Détecteurs inductifs\\e2q5\\e2q5_perperl_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(40, 'E2Q6', 'Détecteur de proximité rectangulaire\r\navec terminal de connexion sans fil', 'La gamme E2Q6 présente un nouveau type de détecteurs de proximité inductifs rectangulaires, avec distance de détection étendue et terminal connecté novateur pour connexion sans fil. Plus résistant à l\'usure, ce nouveau terminal est aussi très bien protégé contre la corrosion. L\'E2Q6 prend la forme d\'un interrupteur de fin de course standard et peut être utilisé en tant que solution sans contact alternative pour des applications similaires.', '<li> Conduit M20 avec bornier pour connexion sans fil</li>\r\n<li>Cinq sens de détection : avant ou à 90 ° (haut, bas, gauche ou droite). </li>\r\n<li>Quatre indicateurs fournissent l\'état de fonctionnement du détecteur, dans de nombreux sens. </li>', 5, 25, 0.000, 0.00, 'assets\\img\\Détection\\Détecteurs inductifs\\E2Q6\\e2q6_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(41, 'E2ER/E2ERZ', 'Capteurs de proximité\r\nrésistants à l’huile', 'Capteurs de proximité résistants à l’huile avec détection standard et fonction de résistance aux copeaux pour résister à l’huile de coupe afin de réduire les défaillances dans les domaines d’application de l’industrie automobile et des machines-outils.', '<li>Câble en fluorésine (PUR) résistant aux huiles de coupe </li>\r\n<li>Boîtier chargé de résine pour éviter la pénétration des huiles de coupe </li>\r\n<li>Classe de protection IP67G (conformément à JIS C0920) </li>', 5, 25, 0.000, 0.00, 'assets\\img\\Détection\\Détecteurs inductifs\\E2ERE2ERZ\\e2er-e2erz_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(42, 'E2EH', 'Capteurs inductifs résistants à\r\nla chaleur et aux détergents en\r\nboîtier acier inox. cylindrique', 'Les capteurs inductifs résistant à la chaleur et aux détergents permettent la détection fiable d\'objets métalliques ou de pièces de machines dans des environnements difficiles tels que l\'agro-alimentaire.', '<li> Température résistante jusqu\'à 120 °C </li>\r\n<li> Boîtier AISI 316L avec face de détection plastique haute performance </li>\r\n<li> IP69k pour une résistance aux projections d\'eau maximale </li>\r\n<li> Testé ECOLAB et certifié résistant aux détergents </li>', 5, 25, 0.000, 0.00, 'assets\\img\\Détection\\Détecteurs inductifs\\E2EH\\e2eh_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(43, 'E2FQ', 'Détecteur inductif résistant aux produits\r\nchimiques en boîtier cyclindrique PTFE', 'Le E2FQ présente un boîtier en plastique pour obtenir une résistance aux produits chimiques (ex. agents de nettoyage utilisés dans l\'industrie des semi-conducteurs).', '<li> Boîtier complet en plastique pour une résistance aux produits chimiques </li>\r\n<li> Modèles c.c. 2 ou 3 fils </li>', 5, 25, 0.000, 0.00, 'assets\\img\\Détection\\Détecteurs inductifs\\E2FQ\\e2fq_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(45, 'E2FM', 'Détecteur de proximité inductif\r\ndans un boîtier entièrement\r\nmétallique cylindrique\r\n(boîtier + face de détection)', 'La famille E2FM possède un corps et une protection de face de détection entièrement métalliques pour une protection mécanique optimale. Le matériau en acier inoxydable SUS303 assure une résistance élevée aux détergents et huiles minérales.', '<li> Boîtier acier inox. pour une très grande protection mécanique </li>\r\n<li> Modulation de fréquence basse pour l\'immunité aux copeaux métalliques </li>\r\n<li>Câble ignifuge pour une protection élevée contre les dégâts dus à des éclaboussures de soudure (modèles à câbles torsadés)</li>', 5, 25, 0.000, 0.00, 'assets\\img\\Détection\\Détecteurs inductifs\\E2FM\\e2fm-xb_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(47, 'E2C-EDA', 'Détecteur de proximité inductif\r\npour le positionnement de haute\r\nprécision avec amplificateur séparé', 'La gamme de détecteurs inductifs à amplificateur séparé E2C-EDA offre une détection et un positionnement à distance de haute précision. La fonction d\'apprentissage simplifie l\'installation, et la fonction fenêtre (2 sorties) permet de configurer et de modifier facilement les contrôles de tolérance de production.', '<li> Précision de détection typique de plusieurs centaines de µm </li>\r\n<li> Apprentissage de la distance avec précision </li>\r\n<li> Fonction fenêtre (2 sorties) pour les contrôles de tolérance de production </li>\r\n', 5, 25, 0.000, 0.00, 'assets\\img\\Détection\\Détecteurs inductifs\\E2C-EDA\\e2c-eda_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(48, 'E2EC', 'Capteurs inductifs sub-miniatures\r\navec amplificateur déporté pour\r\nles conditions de montage difficiles', 'La gamme E2EC possède les plus petites têtes de capteur pour une détection fiable là où l\'espace est un facteur crucial. Les tailles miniatures des têtes de capteurs sont obtenues en séparant la partie de détection de l\'amplificateur. À la différence des amplificateurs standard séparés, la gamme E2EC simplifie l\'installation car l\'amplificateur est intégré au câble.', '<li> Tête de détection de 3 mm de diamètre pour les plus petits espaces </li>\r\n<li> Boîtier M12 ultracourt de 18 mm de long </li>\r\n<li> Modèle de boîtier entièrement métallique pour une plus grande résistance dans les environnements difficiles </li>', 5, 25, 0.000, 0.00, 'assets\\img\\Détection\\Détecteurs inductifs\\E2EC\\e2ec_img_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(49, 'E2V-X', 'Détecteur de proximité cylindrique\r\nmétaux ferreux et non ferreux', 'L\'E2V-X, encastré dans du métal, offre une détection longue distance de l\'aluminium et du fer.', '<li> Voyant haute visibilité </li>\r\n<li> Détecteurs de proximité c.c. 3 fils : M12, M18, M30 </li>\r\n<li> Détection standard et longue distance en modèles précâblés ou à connecteurs </li>\r\n<li> Boîtier en laiton nickelé </li>', 5, 25, 0.000, 0.00, 'assets\\img\\Détection\\Détecteurs inductifs\\E2V-X\\e2v-x_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(51, 'Sysmac Studio', 'Augmentez votre productivité', 'L\'environnement de développement intégré Sysmac Studio vous permet d\'augmenter votre productivité en tant que 1er environnement de développement intégré du secteur intégrant la logique, le contrôle d\'axe, la robotique, l\'IHM, la vision, la détection, la sécurité et la simulation 3D sur une seule et même plate-forme.', '<li> Contrôleurs série NJ/NX  </li>\r\n<li> IHM série NA  </li>\r\n<li> E/S et sécurité série NX  </li>\r\n<li>  Servosystème série G5 </li>\r\n<li> Variateurs de fréquence série MX2 et RX  </li>\r\n<li> Capteur et système de vision FH et FQM  </li>\r\n<li> Composants réseau série GX  </li>\r\n<li> Capteurs N-Smart E3  </li>\r\n<li> Capteurs de mesure série ZW  </li>\r\n<li>Un logiciel pour le contrôle des axes, la logique séquentielle, la sécurité, la vision et l\'IHM   </li>\r\n<li> Conformité totale à la norme ouverte IEC 61131-3  </li>\r\n<li> Prend en charge la programmation du schéma à contacts, texte structuré et ST en ligne avec jeu d\'instructions riche  </li>\r\n<li> Éditeur CAM : programmation facile des profils d\'axes complexes  </li>\r\n<li> Outil de simulation unique pour séquences et axes 3D  </li>\r\n<li> Fonction de sécurité avancée avec mot de passe de 32 caractères  </li>', 8, 46, 0.000, 0.00, 'assets\\img\\Logiciels\\sysmac_studio_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(52, 'ACE', 'Le premier logiciel basé sur\r\nPC d\'OMRON pour\r\nl\'automatisation de la production', 'ACE 4.0 vous aide à configurer rapidement et facilement vos systèmes robotiques. Il présente désormais une nouvelle interface conviviale et intègre de nouvelles fonctionnalités pour simplifier la conception et le déploiement des lignes de production les plus performantes.', '<li> Plate-forme de gestion de l\'ensemble de la gamme de robots industriels, de commandes, de systèmes de vision et d\'alimentation flexibles OMRON  </li>\r\n<li>  Assistant étape par étape pour concevoir une ligne de conditionnement évolutive, du test de validation technique au déploiement </li>\r\n<li> Outils de vision avancés pour la localisation, l\'identification, la mesure et l\'inspection  </li>\r\n', 8, 47, 0.000, 0.00, 'assets\\img\\Logiciels\\ace_40_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(53, 'CX-Configurator FDT', 'Configurez avec FDT/DTM', 'Outil de configuration avancée des réseaux PROFIBUS qui utilisent la technologie FDT/DTM pour améliorer le support de périphériques sophistiqués, fournis par Omron ou d\'autres fournisseurs.', '<li> Utilisation facile </li>\r\n<li> Sauvegarde de fichier intégrée dans CX-One </li>\r\n<li> Permet de configurer entièrement des produits Omron ou tiers </li>', 8, 48, 0.000, 0.00, 'assets\\img\\Logiciels\\splashscreen_cxone_export_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(54, 'CX-Drive', 'Un logiciel unique pour les\r\nvariateurs et les servodrivers', 'Réduit le temps et la complexité de la configuration, de la mise en service et de l\'entretien des servomoteurs et des variateurs de fréquence à l\'aide d\'un outil logiciel unique. CX-Drive assure la prise en charge des fonctionnalités des nouveaux servosystèmes Accurax G5 et variateur MX2.', '<li>Prend en charge l\'ensemble de la gamme de servomoteurs et de variateurs de fréquence Omron  </li>\r\n<li>  Assistant de réglage automatique</li>\r\n<li> Fonction FFT, analyse de la fréquence de résonance d\'un système mécanique </li>\r\n<li> Configuration et surveillance simples des servodrivers </li>\r\n<li> Suivi des données en temps réel </li>\r\n<li> Accès complet aux périphériques depuis une connexion unique </li>\r\n<li> Accès à distance </li>', 8, 49, 0.000, 0.00, 'assets\\img\\Logiciels\\cx-drive2_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(55, 'CX-Thermo', 'Logiciel de support des régulateurs\r\nde température pour faciliter la\r\nconfiguration et la surveillance', 'Le logiciel de support Omron CX-Thermo a été spécialement développé pour être utilisé avec les régulateurs de température E5AC/E5CC/E5DC/E5EC, E5AN/E5GN, E5_N-H, E5ZN, E5AR, E5ER et CelciuXº. CX-Thermo accélère la configuration des paramètres, simplifie l\'ajustement des appareils et la maintenance. CX-Thermo réduit considérablement le temps et les efforts nécessaires à la définition et à la gestion des paramètres de régulation de la température.', '<li> Configuration et édition avancées des paramètres.  </li>\r\n<li> Fonction de téléchargement par lots.  </li>\r\n<li> Surveillance graphique en ligne du point de consigne, de la valeur de process et de la valeur manipulée.  </li>\r\n<li> Un outil de réglage graphique du PID est également inclus ; il offre une interface simple permettant de régler le régulateur de façon à optimiser sa stabilité, à réduire le dépassement ou à optimiser le rejet des défauts.  </li>', 8, 50, 0.000, 0.00, 'assets\\img\\Logiciels\\cx-thermo_no_version_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(56, 'CX-Supervisor', 'Puissant outil de\r\nvisualisation des machines', 'CX-Supervisor dispose de fonctions puissantes pour une large gamme de besoins d’interface homme-machine sur PC. Les applications simples peuvent être créées rapidement avec l’aide de bibliothèques et de nombreuses fonctions prédéfinies, et les applications les plus complexes peuvent être générées avec un langage de programmation puissant, ou en VBScript™. CX-Supervisor est d’une utilisation extrêmement simple, intuitive et très conviviale. L’importation de composants ActiveX® permet de créer des applications flexibles aux nombreuses fonctions.', 'CX-Supervisor est dédié à la conception et à l’utilisation de la visualisation et du contrôle des machines sur ordinateur. Il est non seulement simple à utiliser pour de petites tâches de surveillance et de contrôle, mais il offre également des fonctionnalités très intéressantes pour la conception des applications les plus sophistiquées.', 8, 51, 0.000, 0.00, 'assets\\img\\Logiciels\\cx-supervisor_splash_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(57, 'CX-Server LITE', 'Connectivité simple mais efficace', 'Complément idéal de notre produit OPC, CX-Server LITE est conçu pour répondre à la plupart des attentes des programmeurs, des plus simples aux plus avancés. Utilisé pour créer des projets IHM simples sur PC, CX-Server LITE permet aux concepteurs de programmes personnalisés d\'envoyer et de recevoir des données API et de manipuler des régulateurs au sein des réseaux Omron.', '<li> Prend en charge tous les réseaux Omron. </li>\r\n<li> Communique avec les séries CS/CJ/C/CV et les API de la famille CP, Trajexia, Celciux et les capteurs ZX. </li>\r\n<li> Prend en charge tous les protocoles de communication d\'Omron. </li>\r\n<li> Programmation à partir de Visual Basic, Excel ou .NET. </li>\r\n<li> Configuration simple grâce à l\'utilisation de composants graphiques ou de scripts complexes utilisant des API. </li>', 8, 52, 0.000, 0.00, 'assets\\img\\Logiciels\\cx-serverlite_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(58, 'CX-Server OPC', 'Les périphériques d\'Omron\r\nsont compatibles avec une\r\n« intégration ouverte »', 'CX-Server OPC fournit une connexion entre les caractéristiques de l\'interface OPC, conforme aux normes du secteur, et l\'architecture réseau et les régulateurs d\'Omron. CX-Server OPC permet à n\'importe quel logiciel client compatible OPC d\'interfacer aisément avec les produits Omron.', '<li>  Compatible avec OPC Data Access 1.0a et les clients SCADA 2.05.</li>\r\n<li> Met en œuvre des interfaces en option comme Symbol Browsing et l\'espace de travail hiérarchique. </li>\r\n<li>Prend en charge tous les réseaux Omron.  </li>\r\n<li> Communique avec les séries CS/CJ/C/CV et les API de la famille CP, Trajexia, Celciux et les capteurs ZX. </li>\r\n<li> Prend en charge tous les protocoles de communication d\'Omron. </li>', 8, 53, 0.000, 0.00, 'assets\\img\\Logiciels\\cx-serveropc_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(59, 'CX-Compolet/SYSMAC Gateway', 'Hautes performances et\r\nconnectivité complète', 'SYSMAC Gateway est un interlogiciel de communication pour ordinateurs personnels exécutant Windows. Il prend en charge les communications CIP et les liaisons de données de balise (EtherNet/IP) en plus des fonctions FinsGateway. Il est disponible sous forme de logiciel autonome à titre d’interlogiciel de communications seulement et est aussi inclus dans le logiciel CX-Compolet.', 'CX-Compolet inclut des composants logiciels qui permettent de créer facilement des programmes pour assurer les communications entre un ordinateur et des contrôleurs Omron. Ce logiciel inclut des objets de contrôle .NET et des objets de contrôle ActiveX qui peuvent être utilisés avec des langages de programmation Visual Basic et C#. Outre la fonctionnalité de communication standard, il prend en charge la communication à l’aide de noms de balise Ethernet/IP avec les familles de contrôleur CJ2 et NJ / NX. Les types de données tels que les structures et les tableaux sont également pris en charge.', 8, 54, 0.000, 0.00, 'assets\\img\\Logiciels\\cx_compolet_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(60, 'NS Runtime PC', 'La technologie NS\r\ndisponible sur votre PC', 'Avec la sortie de NS Runtime PC, il est désormais possible d\'exécuter exactement les mêmes projets IHM directement sur votre PC ou votre IPC.', '<li> Réutilisation de vos projets NS, sans reprogrammation. </li>\r\n<li> Visualisation de votre IHM sur votre PC. </li>\r\n<li>Association parfaite avec un DyaloX.  </li>', 8, 55, 0.000, 0.00, 'assets\\img\\Logiciels\\ns-runtime_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(61, 'Soft NA', 'Intégration de l\'IHM NA au PC industriel', 'Avec la sortie de Soft NA, il est désormais possible d\'exécuter exactement les mêmes projets IHM directement sur votre IPC.', '<li>Réutilisation de vos projets NA Sysmac Studio, sans reprogrammation.  </li>\r\n<li> Visualisation de votre IHM sur votre PC. </li>\r\n<li> Combinaison parfaite avec un IPC de la série NY d\'Omron </li>', 8, 56, 0.000, 0.00, 'assets\\img\\Logiciels\\Soft_NA_prod.bmp', NULL, '', NULL, NULL, NULL, 100, 'V'),
(62, 'Kepware OPC Server', 'Hautes performances\r\net connectivité totale', 'La suite d\'Omron regroupe tous les pilotes Omron de Kepware. Elle inclut les pilotes suivants : Omron FINS Ethernet, Omron FINS Serial, Omron Host Link, Omron NJ Ethernet, Omron Process Suite et Omron Toolbus. Après avoir acheté une licence pour la suite d\'Omron, vous pouvez utiliser l\'ensemble des pilotes sur le même PC.', 'KEPServerEX est une plate-forme de communication qui fournit une source unique de données d\'automatisation industrielle à l\'ensemble de vos applications. La plate-forme a été conçue pour permettre aux utilisateurs de se connecter, de gérer, de surveiller et de contrôler plusieurs périphériques d\'automatisation et applications logicielles via une interface utilisateur intuitive.', 8, 57, 0.000, 0.00, 'assets\\img\\Logiciels\\kepware_omron_suite_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(63, 'Movicon Next', 'Logiciel Movicon SCADA', 'Une technologie SCADA/HMI classique, robuste, simple et efficace', '<li>  Solution évolutive : un éditeur unique de Windows™ 10 (Win32/64) vers Windows™ CE </li>\r\n<li> Les projets sont enregistrés sur de simples fichiers XML qui peuvent être ouverts et modifiés avec tout autre éditeur  </li>\r\n<li>  Gestion des données en temps réel </li>\r\n<li> Pilotes, connectivité OPC UA et DA  </li>\r\n<li> Éditeur simple et puissant  </li>\r\n<li>  Rapports, tendances et analyse des données </li>\r\n<li> Graphiques vectoriels évolutifs  </li>\r\n<li>  Niveau de protection maximal </li>\r\n<li> HTML5 compatible Web  </li>\r\n<li> Conformité 21 CFR Part 11  </li>', 8, 58, 0.000, 0.00, 'assets\\img\\Logiciels\\movicon_next_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(64, 'Relais d\'E/S compact G2RV-SR', 'Le seul relais 6mm\r\nvéritablement industriel', 'Ayant été conçu à partir des premiers principes, au lieu d\'être adapté à partir d\'un relais de PCB, la série G2RV d\'Omron est le seul véritable relais industriel plat sur le marché. En conséquence, le G2RV offre un large éventail d\'avantages aux fabricants de machines et aux constructeurs de panneaux. Avec une largeur de 6 mm seulement, le relais est idéal pour les panneaux et les équipements compacts, tout en offrant la durabilité et la fiabilité requises pour les applications industrielles.', '<li>Technologie Push-in plus – Câblage simple  </li>\r\n<li> Modèles à commutateur de test verrouillable disponibles </li>\r\n<li>Larges broches enfichables – excellente connexion  </li>\r\n<li> LED / Indicateur mécanique – vérification du fonctionnement </li>\r\n<li> Boîtier transparent – vérification de la condition </li>\r\n<li> Plat – encombrement réduit </li>\r\n<li> Type d\'entrée spécial avec contacts plaqués or </li>\r\n<li>Compatible G3RV </li>\r\n', 7, 38, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais électromécaniques\\G2RV_SR_prod.jpg\r\n', NULL, '', NULL, NULL, NULL, 100, 'V'),
(65, 'G6D4', 'G6D4', 'Modèle avec technologie Push-In Plus ajoutée aux relais de borne avec gamme d\'unités de sortie à quatre points', '<li> Obtention d\'un rating 5 A grâce à des conceptions optimales pour une large gamme d\'applications (technologie Push-In Plus). </li>\r\n<li> La borne Push-In Plus permet de diminuer les heures de travail et ne nécessite aucun resserrage. </li>\r\n<li> Les shunts (à commander séparément) facilitent le câblage commun et croisé aux bornes adjacentes. </li>\r\n<li>Méthode de double câblage permettant le câblage en dérivation (technologie Push-In Plus)  </li>\r\n<li> Chaque relais possède des bobines et des contacts indépendants pour les sorties PLC compatibles (NPN et PNP). </li>\r\n<li> Les modèles de relais mécaniques et relais MOS FET de puissance (pour les valeurs nominales des contacts haute fréquence) sont disponibles. </li>\r\n<li> Un voyant de fonctionnement à LED, une diode pour l\'absorption de surtension de la bobine et des outils pour pouvoir retirer facilement les relais sont inclus en standard. </li>\r\n<li> Certification UL et CSA pour les modèles standard. Certification VDE pour borne à vis, certification TÜV pour borne Push-In Plus. Classe de protection IP20 pour les modèles Push-In Plus. </li>', 7, 38, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais électromécaniques\\g6d4_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V');
INSERT INTO `produit` (`ID_PRODUIT`, `NOM_PRODUIT`, `DEF_PRODUIT`, `REM_PRODUIT`, `DESCRIPTION`, `CATEGORIE`, `FAMILLE`, `PRIX`, `TAXE`, `IMAGE`, `IMG`, `ALT`, `DOCUMENT`, `IMG_DOCUMENT`, `VIDEO`, `QUANTITY`, `VISIBLE`) VALUES
(66, 'G2R-_-S', 'Relais embrochables avec\r\nfonctions avancées pour une\r\nlarge gamme d\'applications !', 'La série G2RS qui comprend en standard un indicateur mécanique et une plaque constructeur, couvre une large gamme d\'applications d\'interface. Disponible en option avec contacts plaqués or et diode. Les gammes de socles et de barrettes de connexion offrent un maximum de flexibilité pour l\'installation.', '<li>SPDT type 10A / DPDT type 5 A  </li>\r\n<li> Indicateur mécanique, voyant LED et bouton de test momentané / verrouillable en option </li>\r\n<li> Boîtier transparent </li>\r\n<li>Socles à bornes à ressort disponibles  </li>\r\n<li> Faible encombrement – 16 mm de large, socle compris </li>', 7, 38, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais électromécaniques\\g2rs_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(67, 'famille de relais de puissance miniature MY', 'Relais à usage général les plus\r\npopulaires pouvant être choisis\r\nen fonction de l\'environnement\r\nd\'exploitation et de l\'application', '', '<li> La gamme MY est équipée des fonctionnalités standards suivantes sur tous les modèles* : boîtier transparent, voyant mécanique pour contrôler facilement l\'état des contacts et une LED qui indique le fonctionnement correct du relais et identifie immédiatement le type de tension de la bobine (LED rouge pour bobine c.a. et LED verte pour bobine c.c.). </li>\r\n<li>L\'augmentation de la température est limitée grâce aux broches de 2,6 mm de large offrant une conductivité supérieure.  </li>\r\n<li> Plusieurs modèles sont disponibles afin de s\'adapter à diverses applications, notamment des modèles avec des voyants de fonctionnement et avec des leviers de verrouillage (bornes enfichables MY). </li>\r\n<li> Les opérations de câblage peuvent être réduites de 60 %** par rapport aux bornes à vis classiques. En combinant la gamme MY avec les bornes Push-In Plus (PYF-[]-PU), la force d\'insertion légère permet de réduire le temps de câblage. </li>', 7, 38, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais électromécaniques\\MY family group_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(68, 'LY', 'Relais de puissance miniature', 'La série LY est équipée d\'une barrière anti-arc et d\'une diode intégrée. Elle est proposée dans les modèles pôle simple, double, triple et quadruple. Les types de montages possibles sont rail DIN avec socle, circuit imprimé et bride.', '<li> Types de contacts SPDT, DPDT, 3PDT et 4PDT  </li>\r\n<li> Montage sur rail DIN par socle, sur circuit imprimé ou par bride  </li>\r\n<li>  Charge nominale 10 A </li>', 7, 38, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais électromécaniques\\ly2n_img_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(69, 'MKS', 'Relais industriel extrêmement fiable', 'La série MK(S) s\'est révélée exceptionnellement fiable depuis son lancement il y a environ 50 ans. Pour offrir les mêmes fonctions de test aisées que nos autres familles de relais, le MK-S est équipé d\'un bouton de test verrouillable proposant des fonctions et opérations identiques à celles des familles MY(S) et G2R(S).', '<li>  Contact de types 8 broches DPDT et 11 broches 3PDT </li>\r\n<li> Commutation de courant jusqu\'à 10 A  </li>\r\n<li> Bouton de test verrouillable pour faciliter les tests  </li>\r\n<li>  Températures de - 40 °C à 60 °C </li>', 7, 38, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais électromécaniques\\mks_dim_2_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(70, 'G4Q', 'Relais bistable unique', 'Chaque contact du mécanisme de contacts à double pôle effectue une fermeture-ouverture alternée à chaque entrée d\'impulsion. Il est donc idéal pour le fonctionnement alterné ou les opérations de transfert / commutation d\'un moteur.', '<li> Fonctionnement positif assuré par le mécanisme bistable unique </li>\r\n<li> Rigidité diélectrique de 2 000 Vc.a. </li>\r\n<li> Faible consommation (c.a. : 6,4 VA environ ; c.c. : 3,9 W environ) </li>\r\n<li> Contacts DPDT </li>', 7, 38, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais électromécaniques\\g4q-212s_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(71, 'G7J', 'Relais multipolaire de grande\r\ncapacité utilisé comme contacteur', 'Avec un courant de charge nominal de 25 A dû à sa charnière miniature pour optimiser la puissance de commutation, le G7J peut commuter des charges de moteur comme des charges résistives et inductives. Le G7J n\'a aucune vibration de contact pour les chutes de tension momentanées jusqu\'à 50% de la tension nominale.', '<li> Contacts 4PST-NO, 3PST-NO/SPST-NF ou DPST-NO/DPST-NF </li>\r\n<li> Bornes à raccordement rapide, CI ou à vis </li>\r\n<li> Charge nominale 25 A </li>\r\n<li> Compatible avec des chutes temporaires de tension </li>\r\n<li> Utilisation possible en tant que contacteur </li>', 7, 38, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais électromécaniques\\g7j_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(72, 'G7L', 'Relais de puissance multipolaire', 'Relais multipolaire de grande capacité et à rigidité diélectrique élevée compatible avec des chutes temporaires de tension. Le G7L est doté d\'un mécanisme à double ouverture et d\'un bouton de test (sauf pour les modèles -P).', '<li> Contacts SPST-NO ou DPST-NO  </li>\r\n<li> Bornes à raccordement rapide, CI ou à vis  </li>\r\n<li> Charge nominale de 20, 25 ou 30 A  </li>\r\n<li> Compatible avec des chutes temporaires de tension  </li>\r\n<li> Grande variété d\'applications avec des bobines de 100 ou 200 V  </li>', 7, 38, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais électromécaniques\\g7l-2a-t_img_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(73, 'G7Z', 'Relais de puissance compact\r\ncommutant jusqu\'à 160 A', 'La gamme G7Z offre une solution compacte et économique pour des applications comme les variateurs, onduleurs et les circuits de batteries solaires ou à pile à combustible. Le relais en combinaison avec le bloc contacts auxiliaire est conforme à la norme EN 60947-4-1. Les bobines sont disponibles en 12 ou 24 Vc.c. La consommation est inférieure à 4 W.', '<li> Courant commuté de 160 A (40 A / 4 pôles / IEC-AC1) </li>\r\n<li> Tension commutée de 440 Vc.a. </li>\r\n<li> Fonction de sécurité avec contacts liés dans diverses configurations </li>\r\n<li> Consommation inférieure à 4 W. </li>\r\n<li> Faible bruit de commutation (70 db) </li>', 7, 38, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais électromécaniques\\G7Z-4A-11Z-R-24VDC.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(74, 'MKS(X)', 'Relais de commutation c.c.\r\ncapable de commuter du 220\r\nVc.c., 10 A (charge résistive)', 'D\'apparence semblable au relais MK(S) d\'Omron, une solution qui a fait preuve d\'une fiabilité exceptionnelle depuis son lancement il y a près d\'un demi-siècle, le MK-S(X) est conçu spécialement pour la commutation de puissance élevée. Les relais de puissance c.c. sont généralement utilisés dans l\'industrie électrique, principale cible de la solution MK-S(X).', '<li> Convient pour la commutation c.c. </li>\r\n<li> Commutation de charge c.c. jusqu\'à 10 A, 220 Vc.c. (charge résistive) </li>\r\n<li> Les modèles à charge c.a. sont capables de commuter jusqu\'à 15 A, 250 Vc.a. (charge résistive) </li>\r\n<li> La forme de contact SPST-NO/SPST-NC permet la détection de fusion des contacts </li>', 7, 38, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais électromécaniques\\mk-s(x)_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(86, 'Relais d\'E/S compact G3RV-SR', 'Relais statique fin industriel 6 mm', 'Outre leur compacité qui permet de gagner de l\'espace, les relais G3RV présentent une grande robustesse, offrent une grande zone de contact et sont dotés de broches non pliables. La connexion à une API est aisée et s\'effectue en quelques secondes par le biais de connecteurs à cliquet. De plus, la commutation de puissance des relais G3RV avec sorties CC est gérée par un relais MOSFET en sortie qui possède des propriétés de dissipation de chaleur idéales.', '<li> Compatible G2RV </li>\r\n<li> Voyant LED intégré au relais statique </li>\r\n<li> Technologie Push-in et accessoires pour un câblage simple et rapide </li>', 7, 39, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais statiques\\G3RV_SR_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(87, 'G3R-I / -O', 'Relais statique compact, à haute\r\nrigidité diélectrique, pour interface d\'E/S', 'Modèles à grande vitesse, avec valeurs nominales d\'entrée optimales s\'adaptant à une grande variété de capteurs ainsi que des modules d\'E/S pouvant être utilisés en lieu et place du G2RS. Utilisation d\'un coupleur homologué VDE 0884 pour assurer une rigidité diélectrique d\'E/S de 4 000 V', '<li> Courant de sortie de 1,5 et 2 A </li>\r\n<li> Tensions de sortie 5 à 200 Vc.c. / 100 à 240 Vc.a. </li>\r\n<li> Compatible avec le relais électromécanique G2RS </li>\r\n<li> Montage sur rail DIN avec socle </li>\r\n<li>Indicateur de fonctionnement pour confirmer l\'entrée  </li>', 7, 39, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais statiques\\g3r-i-o_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(88, 'G3F / G3FD', 'Relais statique de type embrochable,\r\nformat et taille compatibles\r\navec le relais de puissance MY', 'Ce relais statique est compatible en forme et en taille avec le relais MY et il se monte sur un rail DIN avec un socle.', '<li> Courant de sortie de 2 et 3 A </li>\r\n<li> Tensions de sortie 4 à 110 Vc.c./100 à 240 Vc.a. </li>\r\n<li> Compatible avec le relais électromécanique MY </li>\r\n<li> Montage sur rail DIN par socle </li>\r\n<li> Indicateur de fonctionnement pour confirmer l\'entrée </li>', 7, 39, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais statiques\\g3fg3fd_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(89, 'G3H / G3HD', 'Relais statique de type\r\nembrochable, format compatible\r\navec le relais mécanique LY', 'Ce relais statique est compatible en forme et en taille avec le relais LY et il se monte sur un rail DIN avec un socle.', '<li> Courant de sortie 3 A </li>\r\n<li> Tensions de sortie 4 à 48 Vc.c./100 à 240 Vc.a. </li>\r\n<li> Compatible avec le relais électromécanique LY </li>\r\n<li> Montage sur rail DIN par socle </li>\r\n<li> Indicateur de fonctionnement pour confirmer l\'entrée </li>', 7, 39, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais statiques\\g3hg3hd_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(90, 'G3B / G3BD', 'Relais statique embrochable,\r\nformat compatible avec le\r\nrelais de puissance MK', 'Ce relais statique est compatible en forme et en taille avec le relais électromécanique MK et il se monte sur un rail DIN avec un socle. Un modèle à sortie c.c. est également disponible avec une plage de tension de charge de 3 à 125 Vc.c. pour les applications haute tension.', '<li> Courant de sortie de 3 et 5 A \r\n<li>Tensions de sortie 5 à 110 Vc.c./100 à 240 Vc.a.  \r\n<li>  Compatible avec le relais électromécanique MK\r\n<li>  Montage sur rail DIN par socle\r\n<li>  Indicateur de fonctionnement pour confirmer l\'entrée', 7, 39, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais statiques\\g3bg3bd_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(91, 'G3NA', 'Large gamme de modèles avec\r\ndes courants de sortie de 5 à 90 A', 'Tous les modèles présentent les mêmes dimensions compactes afin d\'offrir un pas de montage uniforme. Un varistor intégré absorbe efficacement les surcharges externes. Le voyant de fonctionnement permet de surveiller le fonctionnement.', '<li> Courant de sortie 5-90 A</li>\r\n<li> Tensions de sortie 24-264 Vc.a./5-200 Vc.c.</li>\r\n<li>Varistor intégré </li>\r\n<li>Voyant de fonctionnement (rouge) </li>\r\n<li>Capot de protection pour plus de sécurité </li>', 7, 39, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais statiques\\g3na_img_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(92, 'G3NE', 'Relais statique compact et\r\néconomique commutant 5 à 20 A', 'Ce relais statique possède une large plage de tensions : 75 à 264 Vc.a. Le même modèle peut contrôler des charges 100 V et 200 V. Des composants de puissance et un circuit imprimé aluminium compact spécifiques sont utilisés. Son varistor intégré absorbe efficacement les surcharges externes.', '<li> Courant de sortie 5-20 A </li>\r\n<li> Tensions de sortie 75-264 Vc.a. </li>\r\n<li> Varistor intégré </li>\r\n<li> Bornes à connexion rapide, 110 V en entrée et 250 V en sortie </li>', 7, 39, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais statiques\\g3ne_prod.png', NULL, '', NULL, NULL, NULL, 100, 'V'),
(93, 'G3PA', 'Relais statiques avec cartouche\r\nde puissance remplaçable', 'La conception optimale du radiateur a contribué à réduire la taille de ce relais statique. Les cartouches de puissance du G3PA se remplacent facilement, ce qui facilite la maintenance. Le G3PA peut être monté sur rail DIN ou par vis.', '<li> Courant de sortie de 20 à 60 A </li>\r\n<li> Tensions de sortie de 24 à 480 Vc.a. </li>\r\n<li> Commutation possible de charges triphasées </li>\r\n<li> Cartouches de puissance remplaçables </li>', 7, 39, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais statiques\\g3pa_img_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(94, 'G3PJ', 'Relais statiques pour\r\néléments chauffants', 'Un relais statique monophasé pour une faible génération de chaleur permet de conduire 25 A (G3PJ-_25B(-PU) même lors du montage rapproché de trois relais statiques afin de contribuer à réduire les panneaux de contrôle. Les modèles sont disponibles avec des bornes d\'entrée push-in plus.', '<li> SCCR de 10 kA (UL 508) </li>\r\n<li> La protection de contournement des surtensions améliore la rigidité diélectrique aux surtensions pour les courants de sortie. </li>\r\n<li> Bornes d\'entrée push-in plus et bornes à vis disponibles </li>\r\n<li> Monté sur un rail DIN ou avec des vis </li>\r\n<li> Conformité RoHS </li>', 7, 39, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais statiques\\g3pj_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(95, 'G3PE', 'Relais statique compact industriel\r\nG3PE d\'Omron avec résistance\r\nexceptionnelle aux surtensions', 'Courant de sortie 15-45 A monophasé et triphaséTensions de sortie 100-240 Vc.a. et 200-480 Vc.a.Modèles disponibles pour commutation en charge Meilleure rigidité diélectrique contre les surtensions sur les circuits de sortieCache-bornes pour la protection des doigtsInstallation sur rail DIN ou vissée', '<li>Courant de sortie 15-45 A monophasé et triphasé </li>\r\n<li>Tensions de sortie 100-240 Vc.a. et 200-480 Vc.a. </li>\r\n<li>Modèles disponibles pour commutation en charge </li>\r\n<li>Meilleure rigidité diélectrique contre les surtensions sur les circuits de sortie </li>\r\n<li>Cache-bornes pour la protection des doigts </li>\r\n<li> Installation sur rail DIN ou vissée</li>', 7, 39, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais statiques\\g3pe_picture_2_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(96, 'G3PH', 'Relais statiques haute puissance à\r\ncontrôle de charge avec courant élevé\r\nde 75 ou 150 A à 240 ou 480 Vc.a.', 'Les relais statiques haute puissance G3PH sont conçus pour actionner les éléments chauffants en alliage (ex. NI-CR) et à lampe (ex. halogène). Ils comportent une cartouche d\'alimentation remplaçable permettant de réduire les coûts de maintenance. La construction monobloc facile à monter incorpore un radiateur.', '<li> 75 ou 150 A ; 240 ou 480 Vc.a. </li>\r\n<li> Éléments électriques remplaçables </li>\r\n<li> Indicateur de fonctionnement intégré </li>\r\n<li> Modèles disponibles avec ou sans coupure au zéro </li>\r\n<li> Circuit de protection contre les surtensions </li>', 7, 39, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais statiques\\g3ph-2075b_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(97, 'G3PF', 'Relais statique « intelligent » avec\r\ntransformateur de courant intégré qui\r\npermet la détection de surintensité et\r\nde court-circuit dans le relais statique.', 'Le G3PF détecte rapidement la surintensité et un court-circuit dans le relais statique, et l\'information est transmise pour vous signaler le problème survenu, ce qui est idéal pour les applications dans lesquelles les temps d\'immobilisation doivent être minimisés.', '<li> Le transformateur de courant intégré (CT) permet de réduire les opérations de câblage.</li>\r\n<li>Détecte la surintensité d\'un groupe d\'éléments chauffants. </li>\r\n<li>Détecte la surintensité des éléments chauffants triphasés. </li>\r\n<li>Détecte les courts-circuits dans le relais statique. </li>\r\n<li>Le niveau de détection d\'erreur peut être réglé facilement à l\'aide d\'un interrupteur. </li>\r\n<li>Fonction de coupure au zéro de tension. </li>\r\n<li>Voyant de fonctionnement. </li>', 7, 39, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais statiques\\jpg_mac_sgfr-319b-3_003_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(98, 'G3PW', 'Contrôleur de puissance monophasé\r\nde type thyristor permettant un\r\ncontrôle précis de la température', 'La compacité et la possibilité de procéder à un montage côte à côte de plusieurs unités constituent les deux piliers de cette nouvelle génération de contrôleurs de puissance. La surveillance des valeurs est aisée grâce à l\'afficheur à 7 segments situé sur le panneau avant.', '<li> Détection précise du dysfonctionnement de l\'élément chauffant  </li>\r\n<li> Contrôle de phase ou contrôle de cycle optimal  </li>\r\n<li> Communications RS-485 pour régler les variables manipulées et surveiller le courant de charge  </li>\r\n<li> Surveillance de la durée totale de fonctionnement  </li>\r\n<li>  Application avec différentes charges : résistance de charge constante ou variable </li>', 7, 39, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais statiques\\g3pw_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(99, 'G3ZA', 'Contrôleur de puissance multi-\r\ncanal pour une utilisation plus\r\nintelligente des relais statiques', 'Le G3ZA reçoit les variables manipulées générées par des boucles de contrôle ou des paramètres manuels via une connexion RS-485 simple à câbler. Il régule la puissance de l\'élément chauffant avec une grande précision en contrôlant jusqu\'à huit relais statiques standard. De plus, le contrôle d\'offset réduit la puissance maxi. dans le réseau d\'alimentation.', '<li>Contrôleur de puissance multi-canal  </li>\r\n<li>  Contrôle jusqu\'à huit relais statiques standard</li>\r\n<li> Intégration facile avec un API </li>\r\n<li>Compact  </li>\r\n<li>Disponible avec des alarmes d\'élément chauffant (quatre canaux) ou sans (huit canaux)  </li>', 7, 39, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais statiques\\g3za_img_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(102, 'J7KNA-AR', 'Mini-contacteur relais, 4 pôles', 'Trois unités de base peuvent être associées à des contacts auxiliaires supplémentaires. Des versions à 4, 6 et 8 pôles dans différentes configurations sont disponibles, ainsi que différentes tensions de bobine (c.a. et c.c.). Des accessoires, tels que des suppresseurs, sont disponibles.', '<li>Contacts miroir\r\n<li>Fixation par vis et encliquetage (rail DIN 35 mm)\r\n<li>Courant nominal = 10 A (I-th)\r\n<li>Adapté aux appareils électroniques (DIN 19240)\r\n<li>Protection des doigts (BGV A2)', 7, 40, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Contacteurs\\j7kna-ar-22_230_kopie_prod.jpg\r\n', NULL, '', NULL, NULL, NULL, 100, 'V'),
(103, 'J7KNA', 'Contacteurs moteur de 4-5,5 kW\r\npour la commutation standard', 'Ce système modulaire comprend des contacteurs principaux et des blocs contact supplémentaires. Les unités de base peuvent être associées à des contacts auxiliaires (montage sur le dessus). Des versions inversées, y compris un verrouillage mécanique intégré, sont disponibles, ainsi que des versions 3 pôles principaux et 4 pôles principaux.', '<li> Versions 4 kW et 5,5 kW disponibles\r\n<li>Différentes tensions de bobine (c.a. et c.c.)\r\n\r\n<li>Versions taille compacte ou normale\r\n\r\n<li>Les contacteurs peuvent être montés par une fixation par vis ou encliquetage sur un rail DIN\r\n\r\n<li>Tous les composants ont une protection pour les doigts\r\n', 7, 40, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Contacteurs\\j7kna-09-10_230_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(104, 'J7KN', 'Contacteurs moteur de\r\n4-500 kW pour la commutation\r\nnormale et lourde.', 'Ce système modulaire comprend des contacteurs principaux et des blocs contact supplémentaires. Les unités de base peuvent être associées à des contacts auxiliaires. Des versions c.c.-c.c. et un verrouillage mécanique intégré sont disponibles, ainsi que des versions 3 pôles principaux et 4 pôles principaux.', '<li>Les unités de base peuvent être associées à des contacts auxiliaires (montage sur le dessus/côté).\r\n<li>Versions 3 pôles principaux et 4 pôles principaux\r\n<li>La plage de puissance est de 4 à 500 kW.\r\n<li>Différentes tensions de bobine (c.a. et c.c.)\r\n<li>Les modèles J7KN-10D à J7KN-22D disposent de contacts auxiliaires intégrés pour les circuits électroniques (versions 3 pôles)', 7, 40, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Contacteurs\\j7kn-24_230_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(105, 'J7KNU (-AR)', 'Le contacteur à relais le\r\nplus petit au monde !', '', '<li>Alimentation c.a. ou c.c.\r\n<li>Contact auxiliaire intégré\r\n<li>Disponible en tant que mini-contacteur moteur et contacteur à relais\r\n<li>Écartement minimal de contact de > 3 mm selon la norme IEC 60335-1 pour les applications de sécurité\r\n<li>Contacteur alternatif avec verrouillage mécanique\r\n<li>Contacts miroir (NF) selon la norme EC 60947-4-1 pour les applications de sécurité\r\n', 7, 40, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Contacteurs\\j7knu-05-10_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(106, 'J7TKN', 'Relais thermiques pour contacteurs J7', 'Les relais J7TKN protègent les moteurs contre une surcharge thermique. Ils peuvent être montés sur le contacteur ou séparément. Les relais sont conformes IEC 947 (sensibilité monophasée).', '<li>Série de relais de surcharge couvrant une plage de réglage de 0,24 A à 220 A\r\n<li>Tous les composants ont une protection pour les doigts', 7, 40, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Contacteurs\\j7tkn-e-90_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(107, 'J7MN', 'J7MN est une gamme de disjoncteurs\r\nde protection moteur de 0,1 à 100 A', 'Les démarreurs J7MN protègent les moteurs contre les surcharges thermiques et les courts-circuits. Des contacts auxiliaires supplémentaires (standard et indicateurs de déclenchement) sont disponibles, les options sont : commande de déclenchement par shunt ou sous-tension ; boîtiers isolés pour montage en surface ou encastré ; mécanismes rotatifs de couplage de porte et barrettes de connexion triphasées.', '<li>Courants nominaux de fonctionnement : 32 A, 63 A et 100 A\r\n<li>Capacité de commutation principalement de 50 kA/400 V ou 100 KA/400 V\r\n<li>Tous les modèles peuvent être montés sur rail DIN\r\n<li>Pour la gamme 32 A nous fournissons des modules de liaison électro-mécaniques\r\n<li>Tous les composants ont une protection pour les doigts', 7, 40, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Contacteurs\\j7mn-3p_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(108, 'J7KCA', 'Relais auxiliaire (relais de contacteur)', 'Forme identique à celle des contacteurs magnétiques J7KC, idéal pour standardiser la conception des armoires', '<li>La technologie de câblage Push-In Plus permet de gagner du temps lors du câblage et de l\'entretien\r\n<li>Format le plus compact au monde\r\n<li>Fiabilité de contact élevée (min. 5 V cc, 3 mA)\r\n<li>Association avec des unités de contact auxiliaires pour configurer de nombreuses variations de contact\r\n<li>Unité d\'absorption de la surintensité de la bobine installée comme norme\r\n<li>Certifié conforme aux principales normes de sécurité', 7, 40, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Contacteurs\\j7kca_en_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(109, 'J7KC', 'Contacteur magnétique', 'Solution la mieux adaptée pour les moteurs jusqu\'à 2,2 kW (240 V c.a.) *2 ; 5,5 kW (440 V c.a.) et les interrupteurs latéraux principaux', '<li>La technologie de câblage Push-In Plus permet de gagner du temps lors du câblage et de l\'entretien\r\n<li>Format le plus compact au monde\r\n<li>Idéal pour le contrôle des moteurs jusqu\'à 2,2 kW (200 à 240 V c.a.), 5,5 kW (380 à 440 V c.a.), classe AC-3\r\n<li>Fiabilité de contact élevée (min. 5 V c.c, 3 mA)\r\n<li>Unité d\'absorption de la surintensité de la bobine installée comme norme\r\n<li>Certifié conforme aux principales normes de sécurité', 7, 40, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Contacteurs\\j7kc_en_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(110, 'J7TC', 'Relais de surcharge thermique', 'Protection du moteur contre les surcharges et les pertes de phase en l\'associant avec le modèle J7KC pour jusqu\'à 2,2 kW (240 V c.a.) *, 5,5 kW (440 V c.a.)', '<li>La technologie de câblage Push-In Plus permet de gagner du temps lors du câblage et de l\'entretien\r\n<li>Installation par simple contact avec le contacteur magnétique J7KC pour configurer un démarreur magnétique\r\n<li>Circuit auxiliaire haute fiabilité (2PST-1NO 1NC)\r\n<li>Équipé comme norme d\'un couvercle à cadran pour supprimer les modifications de réglage\r\n<li>Facilité de passage entre la réinitialisation manuelle et automatique\r\n<li>Certifié conforme aux principales normes de sécurité', 7, 40, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Contacteurs\\j7tc_en_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(111, 'J7MC', 'Démarreur du moteur manuel\r\n(disjoncteur de protection du moteur)', 'Système MPCB, protection contre les surcharges, les défauts de phase et les courts-circuits', '<li>La technologie de câblage Push-In Plus permet de gagner du temps lors du câblage et de l\'entretien\r\n<li>Associé au contacteur magnétique modèle J7KC, il est idéal pour le contrôle des moteurs jusqu\'à la classe AC-3, 2,2 kW (200 à 240 V c.a.) * ou 5,5 kW (380 à 440 V c.a.).\r\n<li>Interrupteur à levier (type standard) et interrupteur rotatif (type haute performance)\r\n<li>Pouvoir de coupure élevé (max. 100 kA/440 V c.a.)\r\n<li>Équipé d\'un couvercle à cadran comme norme de protection contre les modifications de réglage accidentelles. Verrouillable avec un cadenas pour garantir la sécurité au démarrage.\r\n<li>Certifié conforme aux principales normes de sécurité', 7, 40, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Contacteurs\\j7mc_en_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
(113, 'K8DT-AS', 'Relais de contrôle\r\nd\'intensité monophasé', 'Détecte des erreurs dans les moteurs et autres équipements via des variations de courant. S\'utilise en mode de surintensité ou sous-intensité.\r\n\r\n', '<li>Surveille des courants c.a. ou c.c. avec un seul relais\r\n<li>S\'utilise avec des transformateurs de courant disponibles dans le commerce (TC côté secondaire : 0 à 1 A ou 0 à 5 A)\r\n<li>Réglage de la valeur de fonctionnement, hystérésis, temps de verrouillage au démarrage et temps de fonctionnement\r\n<li>Largeur de 17,5 mm pour un encombrement réduit dans les panneaux\r\n<li>Push-In Plus réduisant les câblages\r\n<li>Listé UL pour expédition facile en Amérique du Nord\r\n<li>Doubles trous d\'insertion pour câblage croisé (toutes les bornes)\r\n<li>Ajout de modèles avec des sorties transistor pour une fiabilité de contact optimale\r\n<li>Conformité RoHS\r\n', 7, 41, 0.000, 0.00, 'assets\\img\\Composants de commutation\\Relais de mesure et de contrôle\\k8dt-as_prod.jpg', NULL, '', NULL, NULL, NULL, 100, 'V'),
